<?namespace Categories{// this file is included into index.php page// to fill in drop down menu with categories when clicking on the button "Categories"class Categories{	public $categoriesSection = NULL;  function __construct(){		$this->iterateCategories();	}	const CATEGORIES = array(	  'art'=>'Art & Culture',		'animals'=>'Animals & Plants',		'beauty'=>'Beauty & Health',		'business'=>'Business & Finance',		'computers'=>'Computers & Internet',		'countries'=>'Countries & Cities',		'education'=>'Education',		'family'=>'Family & Relationships',		'fashion'=>'Fashion & Style',		'food'=>'Food & Drinks',		'goods'=>'Goods & Services',		'house'=>'House & Garden',		'horoscope'=>'Horoscope',		'humor'=>'Humor',		'legalconsultation'=>'Legal consultation',		'leisure'=>'Leisure & Entertainment',		'photo-video'=>'Photo, Video',		'sciences'=>'Sciences & Academic subjects',		'society'=>'Society, Politics, Media',		'sport'=>'Sport',		'transport'=>'Transport',		'travelling'=>'Travelling',		'work'=>'Work & Career',		'other'=>'Other'	);		private function iterateCategories(){		$this->categoriesSection = 'Yes 2';		$this->categoriesSection = '<div id="wrCategories">';		$this->categoriesSection .= '<div id="categories">';				foreach(self::CATEGORIES as $key => $value){			$this->categoriesSection .= '<span class="categories"><a href="index.php?category='.$key.'" class="categorieslink">'.$value.'</a></span>';		}				$this->categoriesSection .= '</div>';		$this->categoriesSection .= '</div>';	}}$categories = new Categories();}?>