<?namespace Categories{// this file is included into index.php page// to fill in drop down menu with categories when clicking on the button "Categories"class Categories{	public $category = NULL;	public static $topSectionCategories = NULL;	public static $footerSectionCategories = NULL;		const CATEGORIES = array(	  'art'=>'Art & Culture',		'animals'=>'Animals & Plants',		'beauty'=>'Beauty & Health',		'business'=>'Business & Finance',		'computers'=>'Computers & Internet',		'countries'=>'Countries & Cities',		'education'=>'Education',		'family'=>'Family & Relationships',		'fashion'=>'Fashion & Style',		'food'=>'Food & Drinks',		'goods'=>'Goods & Services',		'house'=>'House & Garden',		'horoscope'=>'Horoscope',		'humor'=>'Humor',		'legalconsultation'=>'Legal consultation',		'leisure'=>'Leisure & Entertainment',		'photo-video'=>'Photo, Video',		'sciences'=>'Sciences & Academic subjects',		'society'=>'Society, Politics, Media',		'sport'=>'Sport',		'transport'=>'Transport',		'travelling'=>'Travelling',		'work'=>'Work & Career',		'other'=>'Other'	);	  function __construct(){		$this->defineCategoryVariable();		$this->createTopSectionCategories();		$this->createFooterSectionCategories();	}		private function defineCategoryVariable(){		if(isset($_GET['category'])){			$cat = substr((string)$_GET['category'],0,20);			$cat = mb_strtolower($cat,'UTF-8');			$cat = preg_replace('/[^a-z\-]/i','',$cat);			if(is_string($cat)){				if(isset(self::CATEGORIES[$cat])){					$this->category = $cat;				}			}		}	}		private function createTopSectionCategories(){		self::$topSectionCategories = '<div id="wrCategories">';		self::$topSectionCategories .= '<div id="categories">';				foreach(self::CATEGORIES as $key => $value){			self::$topSectionCategories .= '<span class="categories"><a href="index.php?category='.$key.'" class="categorieslink">'.$value.'</a></span>';		}				self::$topSectionCategories .= '</div>';		self::$topSectionCategories .= '</div>';	}		private function createFooterSectionCategories(){		self::$footerSectionCategories = '<div id="footerSectionCategories">';				foreach(self::CATEGORIES as $key => $value){			self::$footerSectionCategories .= '<span class="footerSectionCategories"><a href="index.php?category='.$key.'" class="ftSecCatLink">'.$value.'</a></span>';		}				self::$footerSectionCategories .= '</div>';	}} // end of class Categories$categories = new Categories();} // end of namespace Categories?>